package java_hello;

public class ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n1= 5;
        int n2 =7;
        
        if (n1<n2 ){ 
        	System.out.println("n1 > n2 is true");
        }
        if(n1==n2) {
        	System.out.println("n1 == n2 is true");
        }
        else {
        	System.out.println("n1 == n2 is false");
        }   
	}
}
